
#& npm run build

Copy-Item -Recurse -Force -Path ".\src"  -Destination ..\adminlte-2-react-examples\node_modules\adminlte-2-react
Copy-Item -Recurse -Force -Path ".\build"  -Destination ..\adminlte-2-react-examples\node_modules\adminlte-2-react

$packagePath = "..\adminlte-2-react-examples\node_modules\adminlte-2-react"
Copy-Item -Force -Path ".\package.json"  -Destination $packagePath

$json = Get-Content -Path "$packagePath\package.json" -Raw | ConvertFrom-Json -Verbose
$json.PSObject.Properties.Remove('typings')

ConvertTo-Json -InputObject $json | Set-Content -Path "$packagePath\package.json"